	global $db, $keywords, $kw_article, $products_of_article, $cat, $random_products;
	
	$limit_num_pros = 5;
	
	$sql = "select p.id, p.name_vn, p.name_en, p.size, p.price, p.unique_key_vn, p.unique_key_en, p.img, p.cid, c.unique_key_vn as cat_unique_key from products p, categories c where p.is_available = 1 and p.active = 1 and p.cid = c.id and p.name_vn like '%$keywords%' order by p.price desc, p.id desc limit $limit_num_pros";
	
	$products_of_article = $db->getAll($sql);
	
	if(!$products_of_article && !empty($cat["alias"])){
		$sql = "select p.id, p.name_vn, p.name_en, p.size, p.price, p.unique_key_vn, p.unique_key_en, p.img, p.cid, c.unique_key_vn as cat_unique_key from products p, categories c where p.is_available = 1 and p.active = 1 and p.cid = c.id and c.comp = 2 and c.alias = '" . $cat["alias"] . "' order by p.price desc, p.id desc limit $limit_num_pros";
		
		$products_of_article = $db->getAll($sql);
	}
	
	if(!$products_of_article){
		$sql = "select p.id, p.name_vn, p.name_en, p.size, p.price, p.unique_key_vn, p.unique_key_en, p.img, p.cid, c.unique_key_vn as cat_unique_key from products p, categories c where p.is_available = 1 and p.active = 1 and p.cid = c.id and c.comp = 2 order by RAND() limit $limit_num_pros";
		
		$random_products = $db->getAll($sql);	
	}
	
