<?phpglobal $article, $db;$objArticle = $article->obj;$cid_cua_groupcha = $objArticle['cid'];$limit = 4;$sql = "select * from articles where `cid` = " . $cid_cua_groupcha . " and `active` = 1 and id > " . $article->id . " limit $limit";$articlesListGreater = $db->getAll($sql);$sql = "select * from articles where `cid` = " . $cid_cua_groupcha . " and `active` = 1 and id < " . $article->id . " limit $limit";$articlesListLess = $db->getAll($sql);if (empty($articlesListGreater) && empty($articlesListLess)) {	return;}?><div class="related_post">		                    <div class="pd_title company_title title_border left">								<h3 class="noleftpadding">BÀI VIẾT LIÊN QUAN</h3>							</div>		                    <!-- Post / End -->	                        <ul>							<?php if ($articlesListGreater) { 											foreach ($articlesListGreater as $obj) {												$article = new articles($obj);												$name = $article->getName();												$link = $article->getLink();												$img = $article->img;									?>															<li><a href="<?=$link?>"><?=$name?></a></li>											<?php } // foreach ?>							<?php } ?>														<?php if ($articlesListLess) { 											foreach ($articlesListLess as $obj) {												//$count++;												$article = new articles($obj);												$name = $article->getName();												$link = $article->getLink();												$img = $article->img;									?>															<li><a href="<?=$link?>"><?=$name?></a></li>											<?php } // foreach ?>							<?php } ?>	                        </ul> 		                </div>